mapper.select:
select usm.user_shop_id, usm.create_time, usm.point, buyer.user_id, buyer.name, s.shop_id, s.shop_name from tb_user_shop_map usm, tb_person_info buyer, tb_shop s
usm.user_id=buyer.user_id and usm.shop_id=s.shop_id
and usm.user_id=#{userShopCondition.user.userId}
and usm.shop_id=#{userShopCondition.shop.shopId}
and buyer.name like '%${userShopCondition.user.name}%'
and s.shop_name like '%${userShopCondition.shop.shopName}%'
and usm.create_time >= #{userShopCondition.createTime}
limit #{rowIndex},#{pageSize}

select count(1) from tb_user_shop_map usm, tb_person_info buyer, tb_shop s
usm.user_id=buyer.user_id and usm.shop_id=s.shop_id
and usm.user_id=#{userShopCondition.user.userId}
and usm.shop_id=#{userShopCondition.shop.shopId}
and buyer.name like '%${userShopCondition.user.name}%'
and s.shop_name like '%${userShopCondition.shop.shopName}%'
and usm.create_time >= #{userShopCondition.createTime}

mapper.update:
update tb_user_shop_map
set
point=#{point}
where
user_id=#{user.userId}
and shop_id=#{shop.shopId}
